<div class="pb-form">
    <form action="?" method="post" data-ajax-form onsubmit="return false;">

        <div class="mui-input-group">
            <div class="mui-input-row">
                <label>旧密码</label>
                <input name="passwordOld" type="password" class="mui-input-password mui-input" value="" placeholder="">
            </div>
            <div class="mui-input-row">
                <label>新密码</label>
                <input name="passwordNew" type="password" class="mui-input-password mui-input" value="" placeholder="">
            </div>
            <div class="mui-input-row">
                <label>重复密码</label>
                <input name="passwordRepeat" type="password" class="mui-input-password mui-input" value="" placeholder="">
            </div>
        </div>

        <div class="submit">
            <button type="submit" class="mui-btn mui-btn-block mui-btn-primary">提交</button>
        </div>

    </form>
</div>