<div class="pb ">
    <div class="head">修改密码</div>
    <div class="content">
        <form action="?" class="uk-form" method="post" data-ajax-form>
            <div class="line">
                <div class="label">旧密码:</div>
                <div class="field">
                    <input type="password" name="passwordOld" class="uk-width-1-2" />
                </div>
            </div>
            <div class="line">
                <div class="label">新密码:</div>
                <div class="field">
                    <input type="password" name="passwordNew" class="uk-width-1-2" />
                </div>
            </div>
            <div class="line">
                <div class="label">重复新密码:</div>
                <div class="field">
                    <input type="password" name="passwordRepeat" class="uk-width-1-2" />
                </div>
            </div>
            <div class="line">
                <div class="label">&nbsp;</div>
                <div class="field">
                    <button type="submit" class="uk-button uk-button-primary">提交</button>
                </div>
            </div>
        </form>
    </div>
</div>